import React from 'react';
import { Link } from 'react-router-dom';
import WOW from 'wowjs';
 
class About extends React.Component {
    componentDidMount() {
        new WOW.WOW({
            boxClass: 'wow',
            animateClass: 'animated',
            offset: 20,
            mobile:       true,
            live:         true,
        }).init();
    }

    render(){
        return (
            <section id="about" className="repair-about-area ptb-100">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-6">
                            <div className="repair-about-content">
                                <div className="section-title">
                                    <span>About Us</span>
                                    <h3>We have 40 years of experience in repair services</h3>
                                    <p>We enjoy adapting our strategies to offer every client the best solutions that are at the forefront of the industry.</p>
                                </div>

                                <ul>
                                    <li><i className="icofont-users"></i> Experienced Professionals</li>
                                    <li><i className="icofont-mouse"></i> Expert Technical Skills</li>
                                    <li><i className="icofont-badge"></i> Excellent Reputation </li>
                                    <li><i className="icofont-handshake-deal"></i> Friendly Service</li>
                                </ul>

                                <p><i>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore.</i></p>

                                <Link to="#" className="read-more-btn">Read More About Us</Link>
                            </div>
                        </div>

                        <div className="col-lg-6">
                            <div className="repair-about-image">
                                <img src={require('../../assets/img/about1.jpg')} className="wow fadeInDown" alt="box" />
                                <img src={require('../../assets/img/about2.jpg')} className="wow zoomIn" alt="box" />
                                <img src={require('../../assets/img/about3.jpg')} className="wow fadeInUp" alt="box" />
                                <img src={require('../../assets/img/about-shape1.png')} alt="box" />
                                <img src={require('../../assets/img/about-shape2.png')} alt="box" />
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        );
    }
}
 
export default About;
